cy.get('li:not(:contains(Kiwi))')
  .should('have.length', 4)
  .then(function ($el) { return Cypress._.map($el, 'innerText') })
  .then(function (values) {
    const distinct = Cypress._.uniq(values)
    expect(distinct).to.have.length(values.length)
  })

